INSERT INTO board_post (post_id, title, content, writer_id, view_count, likes, created_at, updated_at) VALUES
(1, '비트코인 다음 주 전망 어떻게 보시나요?', '요즘 비트코인 변동성이 커서 다음 주 흐름이 궁금합니다. 다들 어떻게 예상하시는지 의견 공유해주세요!', 1, 150, 15, '2025-05-01 10:00:00', '2025-05-01 10:00:00'),
(2, '이더리움 POS 전환 이후 스테이킹 수익률', '이더리움 POS 전환 이후 스테이킹 하신 분들 실제 수익률이 어느 정도 나오는지 궁금합니다. 정보 공유 부탁드려요.', 2, 220, 25, '2025-05-01 14:30:00', '2025-05-01 14:30:00'),
(3, 'WeatherCoin 프로젝트 업데이트 소식!', '드디어 WeatherCoin 프로젝트의 새로운 로드맵이 공개되었습니다! 자세한 내용은 공지사항을 참고해주세요.', 3, 500, 75, '2025-05-02 09:00:00', '2025-05-02 09:00:00'),
(4, '알트코인 투자, 어떤 기준으로 고르시나요?', '수많은 알트코인 중에서 옥석을 가리는 기준이 있으신가요? 저는 주로 기술력과 커뮤니티 활동을 봅니다.', 4, 180, 18, '2025-05-02 11:20:00', '2025-05-02 11:20:00'),
(5, '급등하는 코인 따라타도 될까요?', 'A코인이 갑자기 급등하는데 지금이라도 타는 게 맞을까요? 아니면 조정 기다리는 게 나을까요? 조언 부탁드립니다.', 5, 300, 30, '2025-05-03 15:00:00', '2025-05-03 15:00:00'),
(6, '블록체인 기술의 미래에 대한 생각', '블록체인 기술이 앞으로 우리 생활에 어떤 변화를 가져올지 다양한 의견을 나눠보고 싶습니다.', 1, 120, 10, '2025-05-03 18:45:00', '2025-05-03 18:45:00'),
(7, '코인 투자 처음인데 뭐부터 공부해야 할까요?', '코인 투자를 시작해보려고 하는데, 아는 것이 너무 없네요. 어떤 것부터 공부해야 할지 막막합니다. 선배님들의 조언을 구합니다.', 2, 450, 55, '2025-05-04 08:30:00', '2025-05-04 08:30:00'),
(8, 'WeatherCoin 백서 분석 및 토론', 'WeatherCoin 백서를 읽어봤는데, 몇 가지 궁금한 점이 있어서 토론해보고 싶습니다. 특히 토큰 이코노미 부분이요.', 3, 280, 35, '2025-05-04 13:10:00', '2025-05-04 13:10:00'),
(9, '단타 매매 꿀팁 공유합니다!', '제가 요즘 사용하는 단타 매매 지표 설정법과 노하우 공유합니다. 참고하셔서 성투하세요!', 4, 600, 80, '2025-05-05 10:00:00', '2025-05-05 10:00:00'),
(10, '해외 거래소 이용 시 주의사항', '국내 거래소 외에 해외 거래소 이용하시는 분들, 어떤 점을 주의해야 할까요? KYC 인증이나 보안 관련 팁 있으면 알려주세요.', 5, 170, 22, '2025-05-05 16:20:00', '2025-05-05 16:20:00'),
(11, 'NFT 시장, 거품일까요 아니면 새로운 기회일까요?', '최근 NFT 시장에 대한 관심이 뜨거운데, 이것이 일시적인 거품인지 아니면 새로운 투자 기회인지 의견이 분분하네요.', 1, 320, 40, '2025-05-06 11:00:00', '2025-05-06 11:00:00'),
(12, '디파이(DeFi) 상품 이용 후기', '최근에 B 플랫폼의 디파이 상품을 이용해봤는데, 생각보다 수익률이 괜찮네요. 다른 분들은 어떤 상품 이용하시는지 궁금합니다.', 2, 250, 28, '2025-05-06 17:40:00', '2025-05-06 17:40:00'),
(13, 'WeatherCoin 커뮤니티 이벤트 안내', 'WeatherCoin 출시 기념으로 커뮤니티 이벤트를 진행합니다! 많은 참여 부탁드려요. 상품도 푸짐합니다!', 3, 400, 60, '2025-05-07 09:30:00', '2025-05-07 09:30:00'),
(14, '코인 지갑 어떤 종류 사용하시나요?', '코인 보관을 위해 지갑을 사용해야 할 것 같은데, 하드웨어 지갑, 소프트웨어 지갑 등 종류가 많네요. 어떤 걸 추천하시나요?', 4, 190, 19, '2025-05-07 14:00:00', '2025-05-07 14:00:00'),
(15, '투자할 때 가장 중요하게 생각하는 지표는?', '차트 분석, 호재 뉴스, 시장 분위기 등 투자할 때 참고하는 지표가 다양할 텐데, 회원님들은 어떤 것을 가장 중요하게 생각하시는지 궁금합니다.', 5, 210, 26, '2025-05-08 10:50:00', '2025-05-08 10:50:00'),
(16, '비트코인 반감기 이후 시장 전망', '다가오는 비트코인 반감기가 시장에 어떤 영향을 미칠지 다들 어떻게 예측하시나요? 과거 사례를 바탕으로 토론해봅시다.', 1, 350, 45, '2025-05-08 15:15:00', '2025-05-08 15:15:00'),
(17, '솔라나(SOL) 생태계 확장 소식', '최근 솔라나 기반 디앱들이 많이 나오고 있는데, 생태계가 정말 빠르게 확장되는 것 같습니다. 주목할 만한 프로젝트 있을까요?', 2, 270, 33, '2025-05-09 11:30:00', '2025-05-09 11:30:00'),
(18, 'WeatherCoin 기술적 분석 Q&A', 'WeatherCoin의 기술적인 부분에 대해 궁금한 점이 있다면 이 게시글에 질문해주세요! 개발팀에서 답변드릴 예정입니다.', 3, 380, 58, '2025-05-09 16:00:00', '2025-05-09 16:00:00'),
(19, '가상자산 과세 관련 최신 정보', '내년부터 시행될 예정인 가상자산 과세에 대해 최근 업데이트된 정보가 있나요? 미리 준비해야 할 것 같습니다.', 4, 290, 38, '2025-05-10 09:00:00', '2025-05-10 09:00:00'),
(20, '오늘의 투자 일기 (수익 인증!)', '오늘은 운이 좋았네요! 소소하지만 수익 인증합니다. 다들 성투하는 하루 되세요!', 5, 700, 90, '2025-05-10 14:20:00', '2025-05-10 14:20:00');
INSERT INTO board_comment (comment_id, post_id, writer_id, content, likes, created_at, updated_at) VALUES
(1, 1, 2, '저도 다음 주 비트코인 방향성이 궁금하네요. 일단 관망하려구요.', 5, '2025-05-01 10:30:00', '2025-05-01 10:30:00'),
(2, 2, 1, '스테이킹 수익률 생각보다 괜찮더라고요. 안정적인 게 장점인 것 같아요.', 8, '2025-05-01 15:00:00', '2025-05-01 15:00:00'),
(3, 3, 4, 'WeatherCoin 로드맵 드디어 나왔군요! 기대됩니다!', 15, '2025-05-02 09:30:00', '2025-05-02 09:30:00'),
(4, 4, 5, '저는 백서 꼼꼼히 읽어보고 팀원들 이력도 확인하는 편입니다.', 3, '2025-05-02 11:50:00', '2025-05-02 11:50:00'),
(5, 5, 1, '급등주는 위험부담이 크죠. 소액으로만 접근하시는 게 좋을 것 같아요.', 7, '2025-05-03 15:45:00', '2025-05-03 15:45:00'),
(6, 6, 3, '블록체인이 투명성을 높여서 금융 시스템에 큰 변화를 줄 것 같아요.', 2, '2025-05-03 19:00:00', '2025-05-03 19:00:00'),
(7, 7, 4, '유튜브에 좋은 강의 많으니 찾아보시고, 소액으로 시작해보세요!', 12, '2025-05-04 09:00:00', '2025-05-04 09:00:00'),
(8, 8, 2, '토큰 분배 계획이 좀 더 명확했으면 좋겠어요. 장기 투자자들을 위한 락업 물량도 궁금하구요.', 6, '2025-05-04 13:40:00', '2025-05-04 13:40:00'),
(9, 9, 5, '오, 좋은 정보 감사합니다! 한번 적용해봐야겠네요.', 10, '2025-05-05 10:30:00', '2025-05-05 10:30:00'),
(10, 10, 1, '해외 거래소는 수수료 정책이나 출금 제한 같은 걸 잘 확인해야 합니다.', 4, '2025-05-05 16:55:00', '2025-05-05 16:55:00'),
(11, 11, 3, 'NFT는 아직 초기 시장이라 변동성이 크지만, 특정 분야에서는 확실한 가치를 만들어낼 것 같아요.', 9, '2025-05-06 11:45:00', '2025-05-06 11:45:00'),
(12, 12, 4, '저는 C 플랫폼 스테이블코인 예치 상품 이용 중인데 이율이 꽤 쏠쏠해요.', 5, '2025-05-06 18:10:00', '2025-05-06 18:10:00'),
(13, 13, 5, '이벤트 참여 완료! 꼭 당첨됐으면 좋겠네요~', 11, '2025-05-07 10:00:00', '2025-05-07 10:00:00'),
(14, 14, 1, '보안이 중요하다면 하드웨어 지갑, 편의성이 중요하다면 앱 기반 소프트웨어 지갑이 좋을 것 같아요.', 3, '2025-05-07 14:30:00', '2025-05-07 14:30:00'),
(15, 15, 2, '저는 거시 경제 지표랑 시장 심리를 많이 보는 편이에요. 차트는 보조적으로 활용합니다.', 6, '2025-05-08 11:20:00', '2025-05-08 11:20:00'),
(16, 16, 3, '반감기 전후로 단기 변동성은 크겠지만 장기적으로는 우상향할 거라고 믿습니다.', 8, '2025-05-08 15:50:00', '2025-05-08 15:50:00'),
(17, 17, 4, '솔라나 기반 게임 프로젝트들이 요즘 눈에 많이 띄더라고요.', 7, '2025-05-09 12:00:00', '2025-05-09 12:00:00'),
(18, 18, 5, 'WeatherCoin의 합의 알고리즘에 대해 자세히 설명해주실 수 있나요?', 10, '2025-05-09 16:30:00', '2025-05-09 16:30:00'),
(19, 19, 1, '아직 확정된 건 없지만, 손익통산이나 이월공제 관련 내용이 중요할 것 같아요.', 5, '2025-05-10 09:45:00', '2025-05-10 09:45:00'),
(20, 20, 2, '축하드립니다! 저도 오늘 익절하고 치킨 시켰어요 ㅎㅎ', 18, '2025-05-10 14:50:00', '2025-05-10 14:50:00');